import React, { Component } from "react";
import Slider from "react-slick";
import axios from "axios";
import { Card } from "react-bootstrap";

export default class MovieCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      playingMovies: []
    };
  }

  getMovies = async () => {
    const res = await axios.get(this.props.apiUrl);
    const data = res.data.results;
    this.setState({ playingMovies: data });
  };

  componentDidMount() {
    this.getMovies();
  }

  render() {
    var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 5,
      initialSlide: 1,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 1
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2
          }
        }
      ]
    };
    const { playingMovies } = this.state;

    return (
      <div className="p-5">
        <h1 className="text-white">{this.props.title}</h1>
        <Slider {...settings}>
          {playingMovies.map(cur => {
            return cur.backdrop_path ? (
              <Card key={cur.id} style={{ cursor: "pointer" }}>
                <Card.Img
                  variant="top"
                  src={`http://image.tmdb.org/t/p/w92l/${cur.poster_path}`}
                />
              </Card>
            ) : null;
          })}
        </Slider>
      </div>
    );
  }
}
