import React, { Component } from "react";
import { Jumbotron, Button } from "react-bootstrap";
import axios from "axios";

const apiKey = "170638d59cd41f58852a2f12564d2503";
export class Details extends Component {
  constructor(props) {
    super(props);
    this.state = {
      details: {}
    };
  }
  //   https://api.themoviedb.org/3/tv/1412?api_key=170638d59cd41f58852a2f12564d2503&language=en-US
  //   https://api.themoviedb.org/3/movie/299534?api_key=170638d59cd41f58852a2f12564d2503&language=en-US

  getMovieDetails = async api => {
    const { id, type } = this.props.match.params;

    const res = await axios.get(
      `https://api.themoviedb.org/3/${type}/${id}?api_key=${api}`
    );
    this.setState({ details: res.data });
  };
  componentDidMount() {
    this.getMovieDetails(apiKey);
  }
  render() {
    const data = this.state.details;
    const { type } = this.props.match.params;

    return (
      <div className="banner-gradient">
        <Jumbotron className="m-5">
          <h1>{type === "tv" ? data.name : data.title}</h1>
          <p>
            This is a simple hero unit, a simple jumbotron-style component for
            calling extra attention to featured content or information.
          </p>
          <p>
            <Button variant="primary">Learn more</Button>
          </p>
        </Jumbotron>
      </div>
    );
  }
}

export default Details;
