import React, { Component } from "react";
import Slider from "react-slick";
import noImage from "../noImage.png";
import Loading from "./pages/Loading";

import {
  Col,
  Row,
  ListGroup,
  ButtonToolbar,
  Button,
  Container,
  Card
} from "react-bootstrap";

import YoutubeModal from "./pages/YoutubeModal";
import CastList from "./CastList";
import { SliderSettings } from "./pages/SliderSettings";
import { connect } from "net";

import {
  getMovie,
  getCast,
  getTrailer,
  getSimilar
} from "../actions/movieActions";

export class Details extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modalShow: false
    };
  }

  modalClose = () => {
    this.setState({ modalShow: false });
  };

  componentDidMount() {
    const { type, id } = this.props.match.params;
    const { getMovie, getCast, getTrailer, getSimilar } = this.props;
    getMovie(type, id);

    // this.getMovieDetails(apiKey);
    // this.getMovieTrailer(apiKey);
    // this.getMovieCast(apiKey);
    // this.getSimilar(apiKey);
  }
  render() {
    const { details, trailer, genres, modalShow, cast, similar } = this.state;
    const fullYear = String(details.release_date).slice(0, 4);

    return (
      <div
        className="banner-gradient-details  text-light "
        style={{
          backgroundImage: `linear-gradient(rgba(0,0,53,0.7),rgba(0,0,53,0.7)),url(http://image.tmdb.org/t/p/original/${
            details.backdrop_path
          })`
        }}
      >
        <Container>
          <Row
            className=" wow fadeIn align-items-center justify-content-center"
            data-wow-delay=".7s"
          >
            <Col className="text-center py-4" md={3}>
              {" "}
              <img
                className="rounded"
                alt=""
                width="225"
                src={
                  details.poster_path
                    ? `http://image.tmdb.org/t/p/original/${
                        details.poster_path
                      }`
                    : noImage
                }
              />
            </Col>
            <Col className="py-4" lg={8} xs={11}>
              <h1>
                {details.title}{" "}
                <span className="text-warning">({fullYear})</span>
              </h1>

              {genres.map((cur, i) => (
                <ListGroup.Item
                  key={i}
                  className="d-inline-block  p-2 m-2 rounded"
                  variant="warning"
                >
                  {cur}
                </ListGroup.Item>
              ))}

              {trailer === "noid" ? null : (
                <ButtonToolbar className="d-inline-block m-1">
                  <Button
                    variant="warning"
                    onClick={() => this.setState({ modalShow: true })}
                  >
                    <i className="fas fa-play-circle" /> Trailer
                  </Button>

                  <YoutubeModal
                    show={modalShow}
                    onHide={this.modalClose}
                    trailer={trailer}
                  />
                </ButtonToolbar>
              )}

              <hr
                className="mx-0"
                style={{
                  border: "1px white solid",
                  width: "250px"
                }}
              />
              <p className="p-2 bg-light text-dark rounded">
                {details.overview ? (
                  details.overview
                ) : (
                  <span>
                    Sorry, no details can be found for " {details.title} "
                  </span>
                )}
              </p>
            </Col>
          </Row>
          <section className="wow fadeIn" data-wow-delay=".7s">
            <h1 className=" pt-5 text-white text-center">Top Cast Members</h1>

            <CastList cast={cast} />
          </section>

          <h1
            className=" pt-5 text-white text-center wow fadeIn"
            data-wow-delay=".2s"
          >
            Recommendations
          </h1>

          {similar.length > 0 ? (
            <Slider {...SliderSettings}>
              {similar.map((cur, i) => {
                return cur.backdrop_path ? (
                  <Card
                    className="text-white wow fadeIn"
                    data-wow-delay={`${0.1 * i.toString()}s`}
                    key={cur.id}
                  >
                    <Card.Img
                      variant="top"
                      src={
                        cur.poster_path
                          ? `http://image.tmdb.org/t/p/original/${
                              cur.poster_path
                            }`
                          : noImage
                      }
                    />
                    <a href={`/details/movie/${cur.id}/`}>
                      <Button
                        style={{
                          position: "absolute",
                          top: "30%",
                          left: "35%",
                          right: "35%"
                        }}
                        variant="warning"
                      >
                        <i className="fas fa-info-circle" />
                      </Button>
                    </a>

                    <Card.Title className="pt-3">
                      {cur.title ? cur.title : cur.name}
                    </Card.Title>
                    <hr
                      className="mx-0"
                      style={{
                        border: "1px white solid",
                        width: "50px"
                      }}
                    />
                    {cur.vote_average > 0 ? (
                      <Card.Text className="my-3">
                        TMDB Rating <br />
                        <i className="fas fa-star" /> {` `}
                        {cur.vote_average} / 10
                      </Card.Text>
                    ) : (
                      <Card.Text>
                        No ratings <i className="fas fa-frown" />
                      </Card.Text>
                    )}
                  </Card>
                ) : null;
              })}
            </Slider>
          ) : (
            <p className="text-center pb-3">
              Sorry, no recommendations for " {details.title} "
            </p>
          )}
        </Container>
        <Loading />
      </div>
    );
  }
}
const mapStateToProps = state => ({
  details: state.details
});

export default connect(
  mapStateToProps,
  {
    getMovie,
    getCast,
    getTrailer,
    getSimilar
  }
)(Details);
