import React, { Component } from "react";
import { Button, Container, Col, Row } from "react-bootstrap";
import axios from "axios";

const apiKey = "170638d59cd41f58852a2f12564d2503";
export class Details extends Component {
  constructor(props) {
    super(props);
    this.state = {
      details: {}
    };
  }

  getMovieDetails = async api => {
    const { id, type } = this.props.match.params;
    const res = await axios.get(
      `https://api.themoviedb.org/3/${type}/${id}?api_key=${api}`
    );
    this.setState({ details: res.data });
  };
  componentDidMount() {
    this.getMovieDetails(apiKey);
  }
  render() {
    const data = this.state.details;
    const { type } = this.props.match.params;

    return (
      <div
        className="banner-gradient-details  text-white"
        style={{
          backgroundImage: `linear-gradient(rgba(53,0,53,0.7),rgba(53,0,53,0.7)),url(http://image.tmdb.org/t/p/original/${
            data.backdrop_path
          })`
        }}
      >
        <Row className="align-items-center justify-content-center mx-auto">
          <Col className="pr-auto">
            {" "}
            <img
              alt=""
              width="200px"
              src={`http://image.tmdb.org/t/p/original/${data.poster_path}`}
            />
          </Col>
          <Col className="m-auto">
            <h1>{type === "tv" ? data.name : data.title}</h1>
            <p>{data.overview}</p>
          </Col>
        </Row>
      </div>
    );
  }
}

export default Details;
