import React, { Component } from "react";
import Slider from "react-slick";
import axios from "axios";
import { Card, Button, Col } from "react-bootstrap";
import { Link } from "react-router-dom";

export default class MovieCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      playingMovies: []
    };
  }

  getMovies = async () => {
    const res = await axios.get(this.props.apiUrl);
    const data = res.data.results;
    this.setState({ playingMovies: data });
  };

  componentDidMount() {
    this.getMovies();
  }

  render() {
    var settings = {
      infinite: false,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 5,
      initialSlide: 1,
      responsive: [
        {
          breakpoint: 1300,
          settings: {
            slidesToShow: 4,
            slidesToScroll: 4,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1050,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 800,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 1
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    const { playingMovies } = this.state;
    console.log(playingMovies);
    return (
      <div className="p-5">
        <h1 className="text-white">{this.props.title}</h1>
        <Slider {...settings}>
          {playingMovies.map(cur => {
            return cur.backdrop_path ? (
              <Card key={cur.id} className="text-white">
                <Card.Img
                  variant="top"
                  src={`http://image.tmdb.org/t/p/original/${cur.poster_path}`}
                />

                <Card.Title className="pt-3">
                  {cur.title ? cur.title : cur.name}
                </Card.Title>
                <hr
                  className="mx-0"
                  style={{
                    border: "1px white solid",
                    width: "50px"
                  }}
                />
                <Card.Text>
                  TMDB Rating <br />
                  <i className="fas fa-star" /> {` `}
                  {cur.vote_average} / 10
                </Card.Text>
                {this.props.type === "movie" ? (
                  <Link
                    style={{
                      position: "absolute",
                      bottom: "25%",
                      top: "25%",
                      left: "25%",
                      right: "25%"
                    }}
                    className="btn btn-success"
                    to={`/details/${this.props.type}/${cur.id}/`}
                  >
                    Get Details
                  </Link>
                ) : (
                  <Link
                    style={{
                      position: "absolute",
                      bottom: "25%",
                      top: "25%",
                      left: "25%",
                      right: "25%"
                    }}
                    className="btn btn-success"
                    to={`/tvdetails/${this.props.type}/${cur.id}/`}
                  >
                    Get Details
                  </Link>
                )}
              </Card>
            ) : null;
          })}
        </Slider>
      </div>
    );
  }
}
