import React, { Component } from "react";
import { Carousel } from "react-bootstrap";
import axios from "axios";

const apiKey = "170638d59cd41f58852a2f12564d2503";
export class Discover extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movies: [],
      configuration: {}
    };
  }

  getMovies = async api => {
    const res = await axios.get(
      `https://api.themoviedb.org/3/discover/movie?api_key=${api}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1`
    );
    const data = res.data.results;
    this.setState({ movies: data });
  };
  getConfig = api => {
    axios
      .get(`https://api.themoviedb.org/3/configuration?api_key=${api}`)
      .then(data => this.setState({ configuration: data }));
  };

  componentDidMount() {
    this.getMovies(apiKey);
    this.getConfig(apiKey);
  }

  render() {
    const { movies, configuration } = this.state;
    console.log(configuration.data);
    const { data } = this.state.configuration;
    console.log(data);
    const elements = [];
    for (var i = 0; i < 5; i++) {
      elements.push(<i className="fas fa-star" key={i} />);
    }

    return (
      <div>
        <Carousel indicators={false}>
          {movies.map(cur => (
            <Carousel.Item
              className="banner-gradient text-left   text-white"
              style={{
                backgroundImage: `linear-gradient(rgba(0,0,0,0.2),rgba(0,0,0,0.2)),url(https://images8.alphacoders.com/100/1003220.png)`
              }}
            >
              <Carousel.Caption className="text-left mb-5 pb-5">
                <h2>Film Name</h2>
                <h1>Discover Films</h1>
                <p>
                  TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
                </p>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
        </Carousel>
        ;
      </div>
    );
  }
}

export default Discover;
