import React, { Component } from "react";
import { Carousel } from "react-bootstrap";

export class Discover extends Component {
  render() {
    const elements = [];
    for (var i = 0; i < 5; i++) {
      elements.push(<i className="fas fa-star" key={i} />);
    }

    return (
      <div>
        <Carousel indicators={false}>
          <Carousel.Item
            className="banner-gradient text-left   text-white"
            style={{
              backgroundImage: `linear-gradient(rgba(0,0,0,0.2),rgba(0,0,0,0.2)),url(https://images8.alphacoders.com/100/1003220.png)`
            }}
          >
            <Carousel.Caption className="text-left mb-5 pb-5">
              <h2>Film Name</h2>
              <h1>Discover Films</h1>
              <p>
                TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
              </p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item
            className="banner-gradient text-left   text-white"
            style={{
              backgroundImage: `linear-gradient(rgba(0,0,0,0.2),rgba(0,0,0,0.2)),url(https://images5.alphacoders.com/692/692438.jpg)`
            }}
          >
            <Carousel.Caption className="text-left mb-5 pb-5">
              <h2>2nd Film Name</h2>
              <h1>Discover Films</h1>
              <p>
                TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
              </p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item
            className="banner-gradient text-left   text-white"
            style={{
              backgroundImage: `linear-gradient(rgba(0,0,0,0.2),rgba(0,0,0,0.2)),url( https://images4.alphacoders.com/653/653613.jpg)`
            }}
          >
            <Carousel.Caption className="text-left mb-5 pb-5">
              <h2>3rd Film Name</h2>
              <h1>Discover Films</h1>
              <p>
                TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
              </p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
        ;
      </div>
    );
  }
}

export default Discover;
