import React, { Component } from "react";
import { Col, Row } from "react-bootstrap";
import axios from "axios";

const apiKey = "170638d59cd41f58852a2f12564d2503";
export class Details extends Component {
  constructor(props) {
    super(props);
    this.state = {
      details: {}
    };
  }

  getMovieDetails = async api => {
    const { id, type } = this.props.match.params;
    const res = await axios.get(
      `https://api.themoviedb.org/3/${type}/${id}?api_key=${api}`
    );
    this.setState({ details: res.data });
  };
  componentDidMount() {
    this.getMovieDetails(apiKey);
  }
  render() {
    const data = this.state.details;
    const fullYear = String(data.release_date).slice(0, 4);
    console.log(data);
    return (
      <div
        className="banner-gradient-details  text-white"
        style={{
          backgroundImage: `linear-gradient(rgba(0,0,53,0.7),rgba(0,0,53,0.7)),url(http://image.tmdb.org/t/p/original/${
            data.backdrop_path
          })`
        }}
      >
        <Row className="  align-items-center justify-content-center">
          <Col className="text-center py-4" md={3}>
            {" "}
            <img
              alt=""
              width="225"
              src={`http://image.tmdb.org/t/p/original/${data.poster_path}`}
            />
          </Col>
          <Col className="py-4" lg={8} xs={11}>
            <h1>
              {data.title} <span>({fullYear})</span>
            </h1>
            <iframe
              src="https://www.youtube.com/embed/hA6hldpSTF8"
              frameborder="0"
              align="middle"
              allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
              allowfullscreen
            />
            <p />
            <hr
              className="mx-0"
              style={{
                border: "1px white solid",
                width: "300px"
              }}
            />
            <p
              style={{
                background: "rgba(39,54,79,.9)",
                padding: "10px",
                borderRadius: "10px"
              }}
            >
              {data.overview}
            </p>
          </Col>
        </Row>
      </div>
    );
  }
}

export default Details;
