import React, { Component } from "react";
import Slider from "react-slick";
import axios from "axios";
import { Card, Image } from "react-bootstrap";

export default class MovieCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      playingMovies: []
    };
  }

  getMovies = async () => {
    const res = await axios.get(this.props.apiUrl);
    const data = res.data.results;
    this.setState({ playingMovies: data });
  };

  componentDidMount() {
    this.getMovies();
  }

  render() {
    var settings = {
      infinite: false,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 5,
      initialSlide: 1,
      responsive: [
        {
          breakpoint: 1400,
          settings: {
            slidesToShow: 4,
            slidesToScroll: 4,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1050,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 800,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 1
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    const { playingMovies } = this.state;

    return (
      <div className="p-5">
        <h1 className="text-white">{this.props.title}</h1>
        <Slider {...settings}>
          {playingMovies.map(cur => {
            return cur.backdrop_path ? (
              <Image
                src={`http://image.tmdb.org/t/p/original/${cur.poster_path}`}
              />
            ) : null;
          })}
        </Slider>
      </div>
    );
  }
}
