import React, { Component } from "react";

import Discover from "../Discover";
import Loading from "./Loading";
// import MovieCarousel from "../MovieCarousel";
import { connect } from "react-redux";

import {
  getNowPlaying,
  getUpcoming,
  getStudioGhibli,
  getTopRated
} from "../../actions/movieActions";

import { getAsianDramas, getPopularTV } from "../../actions/tvActions";

export class Home extends Component {
  componentDidMount() {
    this.props.getAsianDramas();
    this.props.getPopularTV();
    this.props.getNowPlaying();
    this.props.getUpcoming();
    this.props.getStudioGhibli();
    this.props.getTopRated();
  }
  render() {
    const {
      nowPlaying,
      topRated,
      studioGhibli,
      upcoming,
      asianDramas,
      popularTV
    } = this.props;

    return (
      <div>
        <Discover />

        {/* <MovieCarousel movies={nowPlaying} type="" title="" />
        <MovieCarousel movies={topRated} type="" title=""/>
        <MovieCarousel movies={studioGhibli} type="" title=""/>
        <MovieCarousel movies={upcoming} type="" title=""/>
        <MovieCarousel movies={asianDramas} type="" title=""/>
        <MovieCarousel movies={popularTV} type="" title=""/> */}

        <Loading />
      </div>
    );
  }
}

const mapStateToProps = state => ({
  nowPlaying: state.home.nowPlaying,
  topRated: state.home.topRated,
  studioGhibli: state.home.studioGhibli,
  upcoming: state.home.upcoming,
  asianDramas: state.home.asianDramas,
  popularTV: state.home.popularTV
});

export default connect(
  mapStateToProps,
  {
    getAsianDramas,
    getPopularTV,
    getNowPlaying,
    getUpcoming,
    getStudioGhibli,
    getTopRated
  }
)(Home);
