import React, { Component } from "react";
import "./Loading.css";

export class Loading extends Component {
  // Sets the opacity to 0 100ms after component mounted and removes loader from DOM 2 seconds after
  componentDidMount() {
    setTimeout(() => {
      document.querySelector(".loader-container").style.transition =
        "opacity 3s";
      document.querySelector(".loader-container").style.opacity = ".1";
    });
  }

  render() {
    return (
      <div className="loader-container">
        <div className="loader">
          <div className="inner one" />
          <div className="inner two" />
          <div className="inner three" />
        </div>
      </div>
    );
  }
}

export default Loading;
