import React, { Component } from "react";
import Slider from "react-slick";
import { Card, Button } from "react-bootstrap";

export default class MovieCarousel extends Component {
  render() {
    var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 6,
      slidesToScroll: 6,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 4,
            slidesToScroll: 4,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    return (
      <div>
        <Slider {...settings}>
          <Card className="px-5" style={{ width: "18rem" }}>
            <Card.Img variant="top" src="holder.js/100px180" />
          </Card>
          <Card className="px-5" style={{ width: "18rem" }}>
            <Card.Img variant="top" src="holder.js/100px180" />
          </Card>
        </Slider>
      </div>
    );
  }
}
