import React, { Component } from "react";
import axios from "axios";
import queryString from "query-string";
import { Row, Col } from "react-bootstrap";
import noImage from "../noImage.png";
export class SearchResults extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchResults: []
    };
  }

  getSearchResults = () => {
    const { query } = queryString.parse(this.props.location.search);

    axios
      .get(
        `https://api.themoviedb.org/3/search/multi?api_key=170638d59cd41f58852a2f12564d2503&language=en-US&query=${query}&page=1&include_adult=false`
      )
      .then(res => this.setState({ searchResults: res.data.results }));
  };
  componentDidMount() {
    this.getSearchResults();
  }

  render() {
    const { query } = queryString.parse(this.props.location.search);
    const { searchResults } = this.state;
    console.log(searchResults);

    return (
      <div className="container text-white py-4 text-center">
        <h1>Search Results for: " {query} "</h1>
        <Row className="text-center ">
          {searchResults.map((cur, i) => {
            return (
              <Col key={i}>
                <img
                  alt={cur.name}
                  style={{
                    maxWidth: "200px",
                    borderRadius: "10px"
                  }}
                  src={
                    cur.poster_path
                      ? `http://image.tmdb.org/t/p/original/${cur.poster_path}`
                      : noImage
                  }
                />
                <h3>{cur.title ? cur.title : cur.name}</h3>
                TMDB Rating <br />
                <i className="fas fa-star" /> {` `}
                {cur.vote_average} / 10
              </Col>
            );
          })}
        </Row>
      </div>
    );
  }
}

export default SearchResults;
