import React, { Component } from "react";
import { Carousel } from "react-bootstrap";

export class Discover extends Component {
  render() {
    const elements = [];
    for (var i = 0; i < 5; i++) {
      elements.push(<i className="fas fa-star" key={i} />);
    }

    return (
      <div>
        <div
          className="banner-gradient text-left   text-white"
          style={{
            backgroundImage:
              "url(https://images8.alphacoders.com/100/1003220.png)"
          }}
        >
          <div className="film-info pl-5 pt-5">
            <h2>Film Name</h2>
            <h1>Discover Films</h1>
            <p>
              TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
            </p>
          </div>
        </div>
        <Carousel indicators={false}>
          <Carousel.Item
            className="banner-gradient text-left   text-white"
            style={{
              backgroundImage: `linear-gradient(rgba(0,0,0,0.5),rgba(0,0,0,0.5)),url(${
                image.urls.regular
              })
                `
            }}
          >
            <Carousel.Caption className="text-left mb-5">
              <h2>Film Name</h2>
              <h1>Discover Films</h1>
              <p>
                TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
              </p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item
            className="banner-gradient text-left   text-white"
            style={{
              backgroundImage:
                "url(https://images5.alphacoders.com/692/692438.jpg)"
            }}
          >
            <Carousel.Caption className="text-left mb-5">
              <h2>2nd Film Name</h2>
              <h1>Discover Films</h1>
              <p>
                TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
              </p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item
            className="banner-gradient text-left   text-white"
            style={{
              backgroundImage:
                "url(  https://images4.alphacoders.com/653/653613.jpg)"
            }}
          >
            <Carousel.Caption className="text-left mb-5">
              <h2>3rd Film Name</h2>
              <h1>Discover Films</h1>
              <p>
                TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
              </p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
        ;
      </div>
    );
  }
}

export default Discover;
