import React, { Component } from "react";
import { Col, Row } from "react-bootstrap";
import axios from "axios";

const apiKey = "170638d59cd41f58852a2f12564d2503";
export class TVDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      details: {}
    };
  }

  getMovieDetails = async api => {
    const { id, type } = this.props.match.params;
    const res = await axios.get(
      `https://api.themoviedb.org/3/${type}/${id}?api_key=${api}`
    );
    this.setState({ details: res.data });
  };
  componentDidMount() {
    this.getMovieDetails(apiKey);
  }
  render() {
    const data = this.state.details;

    const fullYear = String(data.first_air_date).slice(0, 4);

    return (
      <div
        className="banner-gradient-details  text-white"
        style={{
          backgroundImage: `linear-gradient(rgba(53,0,53,0.7),rgba(53,0,53,0.7)),url(http://image.tmdb.org/t/p/original/${
            data.backdrop_path
          })`
        }}
      >
        <Row className=" py-4 align-items-center justify-content-center">
          <Col className="text-center" lg={3}>
            {" "}
            <img
              alt=""
              width="225"
              src={`http://image.tmdb.org/t/p/original/${data.poster_path}`}
            />
          </Col>
          <Col className="" lg={8} xs={11}>
            <h1>
              {data.name} <span>({fullYear})</span>
            </h1>
            <p
              style={{
                background: "rgba(39,54,79,.9)",
                padding: "10px",
                borderRadius: "10px"
              }}
            >
              {data.overview}
            </p>
          </Col>
        </Row>
      </div>
    );
  }
}

export default TVDetails;
