import React, { Component } from "react";
import axios from "axios";
import queryString from "query-string";
import { Row, Col } from "react-bootstrap";
export class SearchResults extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchResults: []
    };
  }

  componentDidMount() {
    this.getSearchResults();
  }

  getSearchResults = () => {
    const { query } = queryString.parse(this.props.location.search);

    axios
      .get(
        `https://api.themoviedb.org/3/search/multi?api_key=170638d59cd41f58852a2f12564d2503&language=en-US&query=${query}&page=1&include_adult=false`
      )
      .then(data => console.log(data.data));
  };

  render() {
    const { query } = queryString.parse(this.props.location.search);
    const { movies } = this.state.searchResults;

    return (
      <div className="container text-white py-4 text-center">
        <h1>Search Results for: " {query} "</h1>
        <Row className="text-center ">
          {cast.map((cur, i) => {
            return (
              <Col key={i}>
                <img
                  alt={cur.name}
                  style={{
                    maxWidth: "140px",
                    borderRadius: "10px"
                  }}
                  src={`http://image.tmdb.org/t/p/original/${cur.profile_path}`}
                />

                <h4 className="p-2 mt-2">{cur.name}</h4>
                <p>{cur.character}</p>
              </Col>
            );
          })}
        </Row>
      </div>
    );
  }
}

export default SearchResults;
