import { GET_TV, GET_ASIAN_DRAMAS, GET_POPULAR_TV } from "./types";
import axios from "axios";

const apiKey = "170638d59cd41f58852a2f12564d2503";

export const getTv = () => async dispatch => {
  const res = await axios.get(
    `https://api.themoviedb.org/3/${type}/${id}?api_key=${apiKey}`
  );
  const data = res.data;
  dispatch({
    type: GET_TV,
    payload: data
  });
};
export const getAsianDramas = () => async dispatch => {
  const res = await axios.get("");
  const data = res.data;
  dispatch({
    type: GET_ASIAN_DRAMAS,
    payload: data
  });
};
export const getPopularTV = () => async dispatch => {
  const res = await axios.get("");
  const data = res.data;
  dispatch({
    type: GET_POPULAR_TV,
    payload: data
  });
};
