import React, { Component } from "react";
import { Carousel } from "react-bootstrap";
import axios from "axios";

const apiKey = "170638d59cd41f58852a2f12564d2503";
export class Discover extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movies: []
    };
  }

  getMovies = async api => {
    const res = await axios.get(
      `https://api.themoviedb.org/3/discover/movie?api_key=${api}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1`
    );
    const data = res.data.results;

    this.setState({ movies: data });
  };

  componentDidMount() {
    this.getMovies(apiKey);
  }

  render() {
    const { movies } = this.state;
    console.log(movies.poster_path);
    const elements = [];
    for (var i = 0; i < 5; i++) {
      elements.push(<i className="fas fa-star" key={i} />);
    }

    return (
      <div>
        <Carousel indicators={false}>
          <Carousel.Item
            className="banner-gradient text-left   text-white"
            style={{
              backgroundImage: `linear-gradient(rgba(0,0,0,0.2),rgba(0,0,0,0.2)),url(https://images5.alphacoders.com/692/692438.jpg)`
            }}
          >
            <Carousel.Caption className="text-left mb-5 pb-5">
              <h2>2nd Film Name</h2>
              <h1>Discover Films</h1>
              <p>
                TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
              </p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item
            className="banner-gradient text-left   text-white"
            style={{
              backgroundImage: `linear-gradient(rgba(0,0,0,0.2),rgba(0,0,0,0.2)),url( https://images4.alphacoders.com/653/653613.jpg)`
            }}
          >
            <Carousel.Caption className="text-left mb-5 pb-5">
              <h2>3rd Film Name</h2>
              <h1>Discover Films</h1>
              <p>
                TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
              </p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
        ;
      </div>
    );
  }
}

export default Discover;
