import React, { Component } from "react";
import { Button } from "react-bootstrap";
import axios from "axios";

const apiKey = "170638d59cd41f58852a2f12564d2503";
export class Details extends Component {
  constructor(props) {
    super(props);
    this.state = {
      details: {}
    };
  }

  getMovieDetails = async api => {
    const { id, type } = this.props.match.params;
    const res = await axios.get(
      `https://api.themoviedb.org/3/${type}/${id}?api_key=${api}`
    );
    this.setState({ details: res.data });
  };
  componentDidMount() {
    this.getMovieDetails(apiKey);
  }
  render() {
    const data = this.state.details;
    const { type } = this.props.match.params;

    return (
      <div
        className="banner-gradient-details my-5 text-white"
        style={{
          backgroundImage: `linear-gradient(rgba(53,0,50,0.7),rgba(53,0,50,0.7)),url(http://image.tmdb.org/t/p/original/${
            data.backdrop_path
          })`
        }}
      >
        <img
          alt=""
          width="200px"
          src={`http://image.tmdb.org/t/p/original/${data.poster_path}`}
        />
        <h1>{type === "tv" ? data.name : data.title}</h1>
        <p>{data.overview}</p>
        <p>
          <Button variant="primary">Learn more</Button>
        </p>
      </div>
    );
  }
}

export default Details;
