import React, { Component } from "react";
import Slider from "react-slick";
import axios from "axios";
import { Card, Button } from "react-bootstrap";

export default class MovieCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      playingMovies: []
    };
  }

  getMovies = async () => {
    const res = await axios.get(this.props.apiUrl);
    const data = res.data.results;
    this.setState({ playingMovies: data });
  };

  componentDidMount() {
    this.getMovies();
  }

  render() {
    var settings = {
      infinite: false,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 5,
      initialSlide: 1,
      responsive: [
        {
          breakpoint: 1300,
          settings: {
            slidesToShow: 4,
            slidesToScroll: 4,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1050,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 800,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 1
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    const { playingMovies } = this.state;
    console.log(playingMovies);
    return (
      <div className="p-5">
        <h1 className="text-white">{this.props.title}</h1>
        <Slider {...settings}>
          {playingMovies.map(cur => {
            return cur.backdrop_path ? (
              <Card key={cur.id}>
                <Card.Img
                  variant="top"
                  src={`http://image.tmdb.org/t/p/original/${cur.poster_path}`}
                />
                <Button
                  style={{ position: "absolute", top: "35%", left: "25%" }}
                  variant="success"
                >
                  <i class="fas fa-info-circle" />
                </Button>
                <Card.Title>{cur.title ? cur.title : cur.name}</Card.Title>
                <Card.Text>{cur.vote_average}</Card.Text>
              </Card>
            ) : null;
          })}
        </Slider>
      </div>
    );
  }
}
