import React, { Component } from "react";
import { Carousel } from "react-bootstrap";

export class Discover extends Component {
  render() {
    const elements = [];
    for (var i = 0; i < 5; i++) {
      elements.push(<i className="fas fa-star" key={i} />);
    }

    return (
      <div>
        <div
          className="banner-gradient text-left   text-white"
          style={{
            backgroundImage:
              "url(https://images8.alphacoders.com/100/1003220.png)"
          }}
        >
          <div className="film-info pl-5 pt-5">
            <h2>Film Name</h2>
            <h1>Discover Films</h1>
            <p>
              TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
            </p>
          </div>
        </div>
        <Carousel>
          <Carousel.Item
            className="banner-gradient text-left   text-white"
            style={{
              backgroundImage: "https://images5.alphacoders.com/685/685654.jpg"
            }}
          >
            <Carousel.Caption className="text-left mb-5">
              <h2>Film Name</h2>
              <h1>Discover Films</h1>
              <p>
                TMBD Rating: <br /> {elements.map(cur => cur)}{" "}
              </p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item className="banner-gradient text-left   text-white">
            <img
              className="d-block w-100"
              src="https://images5.alphacoders.com/692/692438.jpg"
              alt="Second slide"
            />

            <Carousel.Caption>
              <h3>Second slide label</h3>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item
            className="banner-gradient text-left   text-white"
            style={{
              backgroundImage:
                "url(  https://images4.alphacoders.com/653/653613.jpg)"
            }}
          >
            <Carousel.Caption>
              <h3>Third slide label</h3>
              <p>
                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
              </p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
        ;
      </div>
    );
  }
}

export default Discover;
