import { GET_DISCOVER, GET_MOVIE } from "../actions/types";

const initialState = {
  results: [],
  details: {}
};

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_DISCOVER:
      return {
        ...state,
        discover: action.payload
      };

    case GET_MOVIE:
      return {
        ...state,
        movieDetails: action.payload
      };

    default:
      return state;
  }
}
