import React, { Component } from "react";
import Slider from "react-slick";
import axios from "axios";
import { Card } from "react-bootstrap";

const apiKey = "170638d59cd41f58852a2f12564d2503";
export default class MovieCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      popularMovies: []
    };
  }

  componentDidMount() {
    this.getMovies(apiKey);
  }

  getMovies = async api => {
    const res = await axios.get(
      `https://api.themoviedb.org/3/movie/latest?api_key=${api}&language=en-US&page=1`
    );
    const data = res.data.results;
    this.setState({ popularMovies: data });
  };

  render() {
    console.log(this.state.popularMovies);
    var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 6,
      slidesToScroll: 6,
      initialSlide: 1,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 4,
            slidesToScroll: 4,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            initialSlide: 1
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2
          }
        }
      ]
    };

    return (
      <div>
        <h2>Popular on TMDB</h2>
        <Slider {...settings}>
          <Card style={{ width: "18rem" }}>
            <Card.Img variant="top" src="holder.js/100px180" />1
          </Card>
        </Slider>
      </div>
    );
  }
}
